mapper.select:
SELECT icr.id AS chatId, iucr.is_read, icr.msg_content, u.id, u.avatar, u.`name`, u.email, icr.send_time, ( SELECT count(ir.id) FROM sys_chat_record ir WHERE ir.id = iucr.chat_id AND iucr.is_read = 0 ) AS unReadCount FROM `sys_chat_record` icr LEFT JOIN sys_user_chat_relation iucr ON iucr.chat_id = icr.id and iucr.is_deleted =0 LEFT JOIN sys_user u ON u.id = iucr.sender_id
and iucr.sender_id = #{vo.senderId}
and iucr.receiver_id = #{vo.receiverId}
GROUP BY u.id ORDER BY icr.send_time DESC

SELECT icr.id AS chatId, iucr.is_read, icr.msg_content, u.id, u.avatar, u.`name`, u.email, icr.send_time FROM `sys_chat_record` icr LEFT JOIN sys_user_chat_relation iucr ON iucr.chat_id = icr.id LEFT JOIN sys_user u ON u.id = icr.sender_id 1=1
and iucr.sender_id = #{vo.senderId}
and iucr.receiver_id = #{vo.receiverId}
order by icr.send_time asc

SELECT count(icr.id) as msgCount FROM `Sys_chat_record` icr LEFT JOIN sys_user_chat_relation iucr ON iucr.chat_id = icr.id and iucr.is_read = 0
and iucr.sender_id = #{vo.senderId}
and iucr.receiver_id = #{vo.receiverId}
